import base64

c = ''

with open('key', 'r') as f:
    key = f.read()

assert(len(key) == 16)

def decrypt(msg):
    return ''.join([chr(msg[i] ^ ord(key[i % len(key)])) for i in range(len(msg))])

d = decrypt(bytearray(base64.b64decode(c)))
assert('Caesar Creek Software' in d)
print(d)

