import base64

c = ""


def decrypt(msg, key):
    return "".join([chr(msg[i] ^ ord(key[i % len(key)])) for i in range(len(msg))])


decoded_msg = bytearray(base64.b64decode(c))


for i in range(0, len(decoded_msg) - 21):
    xored = bytes(
        a ^ b for (a, b) in zip(b"Caesar Creek Software", decoded_msg[i : i + 21])
    )
    # 0-4 == 16-20
    if xored[0:5] == xored[16:21]:
        print(f"got it: {xored}")
